<?php/** * Settings for StackApps API */global $wpdb, $table_prefix;$table_name = $table_prefix.'autoposting_campaigns';$campaigns = $wpdb->get_results("select * from ".$table_name);$statuses = array(    '1' => 'Publish',    '2' => 'Pending',    '3' => 'Draft');                            if(!empty($campaigns)) {    if(!empty($_SESSION['postnow'])) {            }    echo '<style type="text/css">.postnow td { padding: 2px 10px;} .th td {font-weight: bold;}</style>';    echo '<table class="postnow">';    echo '    <tr class="th">    <td>Campaign</td>    <td>Search term</td>    <td>Locale</td>    <td>Post status</td>    <td>Enabled</td>    <td>Numbers</td>    <td>Post</td>    <td></td>    </tr>';    foreach ($campaigns as $campaign) {        echo '        <tr>        <td><a href="?page=themefurnace&campaign='.$campaign->id.'#/stack-autoposting-campaigns">'.$campaign->campaign_name.'</a></td>        <td>'.$campaign->search_term.'</td>        <td>'.$campaign->site.'</td>        <td>'.$statuses[$campaign->post_status].'</td>        <td>'.(($campaign->enabled == 1) ? 'Yes' : 'No').'</td>';             if(!empty($_SESSION['postnow']['campaign_'.$campaign->id])) {      	echo '<td><input type="text" name="posts['.$campaign->id.']" value="'. $_SESSION['postnow']['campaign_'.$campaign->id]['posts'].'"></td>';      $num=$_SESSION['postnow']['campaign_'.$campaign->id]['posts'];      global $wpdb, $table_prefix;      $table_name = $table_prefix.'autoposting_campaigns';      $wpdb->query("update $table_name set postnum=$num where id=$campaign->id");              } else { 	echo '<td><input type="text" name="posts['.$campaign->id.']" value="'.$campaign->postnum.'"></td>'; 	 } echo '        <td><input type="checkbox" name="post_campaigns[]" value="'.$campaign->id.'"></td>        <td>';        if(!empty($_SESSION['postnow']['campaign_'.$campaign->id])) {            echo $_SESSION['postnow']['campaign_'.$campaign->id]['posts'].' posts have been inserted.';        }        echo '</td>        </tr>        ';    }    unset($_SESSION['postnow']);    echo '<tr><td colspan="7"><input type="submit" name="post_now" value="Post Now"></td></tr>';    echo '</table>';    global $up_options;    echo '<input type="hidden" name="autoposting_stack_id" value="'.$up_options->autoposting_app_id.'">';}else {    echo 'No campaigns found.';}