<?php get_header(); ?><div id="content"><?php $paged = (get_query_var('paged')) ? get_query_var('paged') : 1; ?><?php query_posts( 'post_type=question&paged='.$paged); ?><?php while ( have_posts() ) : the_post() ?><div <?php post_class( 'question'); ?> id="post-<?php the_ID(); ?>"><div class="avatar"><p><?php echo get_user_avatar(!empty($_user_data)?$_user_data->ID:'');?></p></div><div class="questionmain"><h2 class="questiontitle"><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h2><ul class="questionmeta"><li>Submitted on <?php the_time('F j, Y') ?> | <?php _e('by:','wp-answers') ?> <a class="url fn n" href="<?php echo get_link_to_profile(get_the_author_meta('user_login')); ?>"><?php the_author_meta( 'first_name' ); ?> <?php the_author_meta( 'last_name' ); ?></a></li></div></ul></div><!-- End Question--><?php endwhile; ?><?php //wp_reset_query(); ?> <?php wpanswers_pagination(); ?></div><!-- End Content--><?php get_sidebar('Questions'); ?><?php get_footer(); ?>